CREATE   VIEW reference.Country AS   SELECT   V.Ref_1 CountryCode, V.Ref_2 CountryName, V.Ref_3 CountryDescription  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V      WHERE V.Ref_7 ='COUNTRY'   AND V.TABLE_NAME = 'VERIFIERS'