CREATE   VIEW reference.DeliveryMode AS   SELECT   V.Ref_1 DeliveryModeCode, V.Ref_2 DeliveryModeName, V.Ref_3 DeliveryModeDescription, P.Ref_10 AvetmissDeliveryModeId  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V   LEFT JOIN edw.reference_master_reference_data P ON P.Ref_7 = 'DEC_MODES_OF_PROVISION' AND P.TABLE_NAME = 'VERIFIER_PROPERTIES' AND P.Ref_8 = V.Ref_1 AND P.Ref_9 IN ('DEC_AVETMISS_2015', 'DEC_STS_MODE')   WHERE V.Ref_7 ='DEC_MODES_OF_PROVISION'   AND V.TABLE_NAME = 'VERIFIERS'