CREATE   VIEW reference.FundingSource AS SELECT   A.Ref_1 AS FundingSourceCode   ,A.Ref_2 AS FundingSourceName   ,A.Ref_3 AS FundingSourceDescription   ,B.Ref_10 AS FundingSourceNationalID   ,C.Ref_10 AS SpecificFundingCodeID   ,A.Ref_6 AS Active   ,A._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,A._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,A._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,A._RecordStart _RecordStart   ,A._RecordEnd _RecordEnd   ,A._RecordDeleted _RecordDeleted   ,A._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data A   LEFT JOIN edw.reference_master_reference_data B ON B.Ref_8 = A.Ref_1 AND B.TABLE_NAME = 'VERIFIER_PROPERTIES' AND B.Ref_9 = 'DEC_FUNDING_SOURCE_NATIONAL_2015'   LEFT JOIN edw.reference_master_reference_data C ON C.Ref_8 = A.Ref_1 AND C.TABLE_NAME = 'VERIFIER_PROPERTIES' AND C.Ref_9 = 'DEC_SPECIFIC_FUNDING_ID_2015'   WHERE   A.TABLE_NAME = 'VERIFIERS'   AND A.Ref_7 = 'FUNDING'