CREATE   VIEW reference.IndigenousStatus AS   SELECT   V.Ref_1 IndigenousStatusID, V.Ref_2 IndigenousStatusName, V.Ref_3 IndigenousStatusDescription  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V      WHERE V.Ref_7 ='INDIGENOUS_IDENTIFIER'   AND V.TABLE_NAME = 'VERIFIERS'