CREATE   VIEW reference.Industry AS   SELECT   V.Ref_1 IndustryCode, V.Ref_3 IndustryName, V.Ref_3 IndustryDescription  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V      WHERE V.Ref_7 ='CLASSIFICATION_CODE'   AND V.TABLE_NAME = 'VERIFIERS'