CREATE   VIEW reference.Language AS   SELECT   V.Ref_1 LanguageID, V.Ref_2 LanguageName, V.Ref_3 LanguageDescription  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V      WHERE V.Ref_7 ='HOME_LANGUAGE'   AND V.TABLE_NAME = 'VERIFIERS'