CREATE   VIEW reference.Occupation AS   SELECT   V.Ref_1 OccupationCode, V.Ref_3 OccupationName  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V      WHERE V.Ref_7 ='STANDARD_OCCUPATION_CODE'   AND V.TABLE_NAME = 'VERIFIERS'