CREATE   VIEW reference.QualificationType AS   SELECT   V.Ref_1 QualificationTypeID, V.Ref_13 QualificationTypeCode, V.Ref_2 QualificationTypeName, V.Ref_3 QualificationTypeText, P.Ref_10 AvetmissQualificationID  ,V.Ref_6 Active   ,V._DLRawZoneTimeStamp _DLRawZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLTrustedZoneTimeStamp   ,V._DLTrustedZoneTimeStamp _DLCuratedZoneTimeStamp   ,V._RecordStart _RecordStart   ,V._RecordEnd _RecordEnd   ,V._RecordDeleted _RecordDeleted   ,V._RecordCurrent _RecordCurrent   FROM edw.reference_master_reference_data V   LEFT JOIN edw.reference_master_reference_data P ON P.Ref_7 = 'QUAL_AWARD_CATEGORY_CODE' AND P.TABLE_NAME = 'VERIFIER_PROPERTIES' AND P.Ref_8 = V.Ref_1 AND P.Ref_9 IN ('DEC_AVETMISS_2015')   WHERE V.Ref_7 ='QUAL_AWARD_CATEGORY_CODE'   AND V.TABLE_NAME = 'VERIFIERS'